mudlet = mudlet or {} mudlet.mapper_script = true wotmudmapper = wotmudmapper or {}
--mapper variables wotmudmapper.currentID = {} wotmudmapper.roomname = "" wotmudmapper.roomdesc = "" wotmudmapper.roomexits = {} wotmudmapper.zone = "" wotmudmapper.brief = false wotmudmapper.dark = false wotmudmapper.flee = false wotmudmapper.following = false wotmudmapper.door = "n" wotmudmapper.pausing = false wotmudmapper.inways = false
--default configuration values to be used or updated in wotmudmapper.configs wotmudmapper.defaults = { mapping=false, dbug=true, mapbrief=false, bothways=true, autojoin=false, forceroom=false, envcolor=20, offline=false, roomID=1, showzone = true, zonecolor = "turquoise", doorcolor = "blue", map_window={ visible=true, x="-37%", y="0%", w="37%", h="73%", zoom=50, dock=false} }
--set default map room type environment numbers and colors wotmudmapper.room_type_env_nums = { inside = 20, wilderness = 21, road = 22, horse = 23, swamp = 24, water = 25, drink = 26, weaponsmith = 27, armorer = 28, blacksmith = 29, bank = 30, stables = 31, rent = 32, grocer = 33, warrior = 34, rogue = 35, hunter = 36, city = 38, hills = 39, field = 40, forest = 41, mountains = 42, desert = 43 }
