List = {}
function List:new() local list = {first = 0, last = -1} setmetatable(list, self) self.__index = self return list end
wotmudmapper.queue = List:new()
function List:pushright(value) local last = self.last + 1 self.last = last self[last] = value end
function List:popleft() local first = self.first local value = self[first] --to allow garbage collection self[first] = nil self.first = first + 1 if first >= self.last then self.first=0 self.last=-1 end if value then wotmudmapper:echo("Removed command ("..value..") from queue.\n",true) else wotmudmapper:echo("No commands left to remove from queue.\n",true) end return value end
function List:checkleftvalue() return self[self.first] end

Script.script:
function wotmudmapper:setConfigs() local defaults = self.defaults or {} local configs = self.configs or {} local default_colors = self.default_map_colors or {} local colors = self.map_colors or {}
self:echo("Loading map configurations.\n") -- load stored configs from file if it exists if io.exists(getMudletHomeDir().."/wotmudmapper.configs.lua") then table.load(getMudletHomeDir().."/wotmudmapper.configs.lua",configs) end if io.exists(getMudletHomeDir().."/wotmudmapper.map_colors.lua") then table.load(getMudletHomeDir().."/wotmudmapper.map_colors.lua",colors) end self:echo("Map configurations loaded.\n")
-- overwrite default values with stored config values configs = table.update(defaults, configs) self.configs = table.deepcopy(configs) table.save(getMudletHomeDir() .. "/wotmudmapper.configs.lua", self.configs) colors = table.update(default_colors, colors) self.map_colors = table.deepcopy(colors) table.save(getMudletHomeDir() .. "/wotmudmapper.map_colors.lua", self.map_colors) end

function MapOnSysDisconnectionEvent() wotmudmapper.configs = wotmudmapper.configs or {} wotmudmapper.configs.mapping = false wotmudmapper.configs.forceroom = false wotmudmapper.configs.offline=false wotmudmapper.map_colors = wotmudmapper.map_colors or {}
if table.size(wotmudmapper.currentID) == 1 then wotmudmapper.configs.roomID=wotmudmapper.currentID[1] else wotmudmapper.configs.roomID=1 end
wotmudmapper:echo("Saving map configurations.\n") table.save(getMudletHomeDir() .. "/wotmudmapper.configs.lua", wotmudmapper.configs) table.save(getMudletHomeDir() .. "/wotmudmapper.map_colors.lua", wotmudmapper.map_colors) wotmudmapper:echo("Map configurations saved.\n") end

function wotmudmapper:show_help(cmd) if cmd then cmd = cmd:gsub(" ","_") if not self.help[cmd] then self:echo("No help file on that command.\n",false,true) return end else cmd = 1 end for w in self.help[cmd]:gmatch("(.-)\n") do local before, target, text, after = w:match("(.*)<link: (.-)>(.*)<\/link>(.*)") if target then cecho(before) fg("yellow") setUnderline(true) if target == "1" then echoLink(text, [[wotmudmapper:show_help()]], "View: map help", true) else echoLink(text, [[wotmudmapper:show_help("]] .. target .. [[")]], "View: map help " .. target, true) end setUnderline(false) resetFormat() if after then cecho(after) end else cecho(w) end cecho("\n") end end
--definition of wotmudmapper.help and sub-parts wotmudmapper.help = { [[ <cyan>WoTMUD Mapping Script<reset>
This script will both follow movements through currently known rooms and allow for the creation of new rooms. It requires "color complete" to be turned on in the MUD for room names to be properly captured, and "autoexits" to be turned on to be able to determine room exits. Both are necessary for the script to function properly. The script will work with or without "brief" mode turned on in the MUD, though it will be more accurate with brief mode off. You can choose to have the script hide room descriptions instead.
This script defines the necessary function so that you can speedwalk by double clicking on rooms.
Below is a brief overview of commonly used commands that this script includes. Additional information for further areas is available as well, though every command may not be described here. All commands can be found in the appropriate mapper folder under mudlet aliases.
<cyan>Common Commands:<reset> <link: 1>map help (optional subfolder name)</link> - Shows either this help file or the help file for the subfolder given (e.g. map help booleans, map help doors, etc.) <yellow>map show<reset> - Displays or hides a map window in a fixed position. Toggle off and restart mudlet in order to make the map a dockable window via the mudlet toolbar map button <yellow>map debug<reset> - Toggles debug mode, in which extra messages are shown with the intent of assisting in troubleshooting. Default: on <yellow>resetqueue<reset> - Clears the list of movement commands maintained by the script. Useful if map appears to not be following correctly, or if aliases to open doors appear to not work properly <yellow>map update<reset> - Downloads, saves, and loads the latest version of the WoTMUD_map.dat file from https://github.com/weisluke/WoTMUD <yellow>map brief<reset> - Toggle whether map script should hide or show the descriptions from rooms. Default: show
<cyan>Information subfolders:<reset> These are sub-folders containing information for more commands <link: booleans>booleans</link> - Map variables that can be toggled on/off <link: creation>creation</link> - Commands used in creating new areas and rooms <link: room_data>room data</link> - Commands used in viewing and changing room data <link: doors>doors</link> - Commands used in creating and editing doors <link: room_colors>room colors</link> - Commands used in viewing and changing room colors <link: key_variables>key variables</link> - Variables used by the script to keep track of important information <link: map_window>map window</link> - Altering the size and position of the map display <link: misc>misc</link> - Miscellaneous commands
]] }
wotmudmapper.help.booleans = [[ <cyan>Mapper Booleans<reset> These commands control configuration options that can be toggled on/off <yellow>mapping<reset> - Turn mapping mode on/off. Default: off (will reset to off when mudlet is closed) <yellow>map bothways<reset> - Toggle whether map should set one-way or two-way exits when creating new rooms. Default: on <yellow>map autojoin<reset> - Toggle whether map should auto-join exits to nearby rooms when mapping, or whether you must actually enter a room for a connection to be made. Default: off <yellow>map brief<reset> - Toggle whether map script should hide or show the descriptions from rooms. Default: show <yellow>forceroom<reset> - Force the map to create a new room when a direction is input (as opposed to potentially linking to a room that already exists). Useful when mapping in areas that have multiple rooms with the same name (e.g. Blight) <yellow>map debug<reset> - Toggles debug mode, in which extra messages are shown with the intent of assisting in troubleshooting. Default: on <yellow>map showzone<reset> - Toggle whether the map script should display or hide zone info <yellow>map offline<reset> - Toggle whether map should be turned into offline mode, enabling offline movement aliases and numpad key bindings that allow you to move around and look via single character direction/look inputs (i.e. n/e/s/w/u/d/l), or through numpad numbers. Default: off (will reset to off, and disable offline movement aliases/keys, when mudlet is closed)
]]
wotmudmapper.help.creation = [[ <cyan>Map Creation<reset> These commands are used in creating new areas and rooms <yellow>createarea areaname<reset> - Create an area with the given name <yellow>createroom areaname<reset> - Create a new room at position (0,0,0) in the given area name, creating the area as well if the name does not already exist <yellow>movetoarea areaname<reset> - Move the current room to the given areaname, if it exists <yellow>arealist<reset> - Show a list of all areas currently known, along with their ID numbers <yellow>roomlist areaname<reset> - Show a list of all known rooms in a given area, along with their ID numbers <yellow>renamearea ID newname<reset> - Rename an area with the given ID number (from the arealist command) to the new name given <yellow>deletearea areaname<reset> - Delete an area and all rooms/labels within it <yellow>deleteroom roomID<reset> - Delete a room with the given ID number <yellow>updateroomname roomID<reset> - Update the name of the given room ID with the currently stored room name <yellow>updateroomdesc roomID<reset> - Update the description of the given room ID with the currently stored description
]]
wotmudmapper.help.room_data = [[ <cyan>Room Data:<reset> These commands are used to view and edit data stored in/around rooms <yellow>listroomdata<reset> - Display data stored for the current room. This will include room description, doors, zone, and any further data you might wish to define and store. <yellow>setroomdata key value<reset> - Set user data in the current room for a key (with no spaces) and an arbitrarily long value. Example: setroomdata herb_load sunburst, setroomdata nohide yes, setroomdata nochannel true, etc. <yellow>getroomdata key<reset> - Display data stored in the current room for the given key <yellow>deleteroomdata key<reset> - Delete the data in the current room with the given key <yellow>deleteallroomdata<reset> - Delete all data stored in the current room. This WILL delete the room description and doors which could cause the map script to not function as well as you might wish <yellow>crz zonename<reset> - Change the zone of the current room to the given name <yellow>cmz zonename<reset> - Change the zone that will be assigned to newly created rooms <yellow>shiftroom direction<reset> - Shift the current room one unit in the given direction <yellow>map zonecolor color<reset> - Change the display color for room zones <yellow>makelabel color position text<reset> - Create a text label with the given color and text at the given position relative to your current room. Possible positions include: n, e, s, ne, se, sw, nw. <yellow>viewcolors<reset> - View a list of possible colors that can be specified in the makelabel command. Opens the relevant mudlet wiki page with the colors. <yellow>setmarker symbol<reset> - Create a marker in your current room with the given symbol.
