--assumes lastexits is of the form {"north":roomID1,"south":roomID2}

function wotmudmapper:isNearRoom(com, name, desc, lastexits)
  local newID = lastexits[wotmudmapper.exitmap[wotmudmapper.exitmap[com]]]
  if not newID then
    return {}
  end
  if self.dark then
    return {newID}
  elseif name == getRoomName(newID) then
    if self.brief then
      return {newID}
    elseif desc == getRoomUserData(newID, "description") then
      return {newID}
    end
  end
  return {}
end

